mapper.select:
SELECT POST_NO, TAG
FROM R_TAG
WHERE POST_NO = #{postNo}

SELECT POST_NO, INGREDIENT, AMOUNT
FROM R_INGREDIENT
WHERE POST_NO = #{postNo}

		SELECT M.MEMBER_NO, NICKNAME, P.CHANGE_NAME, COUNT(R.POST_NO) AS POSTS, AVG(R.RATE) AS RATE, COUNT(F.MEMBER_NO) AS FOLLOWS, SUM(R.HIT) AS HITS
		FROM GOOD G
		JOIN MEMBER M ON(M.MEMBER_NO = G.TARGET_NO)
		JOIN PHOTO P ON(P.BOARD_NO = 0 AND P.TARGET_NO = G.TARGET_NO AND P.FILE_LEVEL = 0 AND P.DELETED != 'Y')
		LEFT OUTER JOIN R_PREVIEW R ON(R.MEMBER_NO = G.TARGET_NO)
		LEFT OUTER JOIN GOOD F ON(F.BOARD_NO = 0 AND F.TARGET_NO = G.TARGET_NO)
		WHERE G.BOARD_NO = #{boardNo}
		AND G.TARGET_NO = #{targetNo}
		AND G.MEMBER_NO = #{memberNo}
        GROUP BY M.MEMBER_NO, NICKNAME, P.CHANGE_NAME

    	SELECT R.POST_NO, R.TITLE, P.CHANGE_NAME, AVG(L.RATING) AS RATE, R.HIT

    	FROM R_BOARD R
    	JOIN PHOTO P ON(P.BOARD_NO = 1 AND P.TARGET_NO = R.POST_NO AND P.FILE_LEVEL = 0)
    	LEFT OUTER JOIN REPLY L ON(L.BOARD_NO = 1 AND L.TARGET_NO = R.POST_NO AND L.MEMBER_NO != R.MEMBER_NO)
    	JOIN R_INGREDIENT G ON(R.POST_NO = G.POST_NO)
    	JOIN R_TAG T ON(R.POST_NO = T.POST_NO)
		WHERE R.DELETED = 'N'


			and R.TITLE like '%${keyword}%'


			and R.CATEGORY = #{category}


			and R.SERVING = ${serving}


			and R.DIFFICULTY <= ${difficulty}


			and R.REQTIME <= ${reqTime}



		and

            G.INGREDIENT = #{ing}




		and

            T.TAG = #{tag}



		GROUP BY R.POST_NO, R.TITLE, P.CHANGE_NAME, HIT

		ORDER BY ${sorter} DESC

mapper.update:
UPDATE R_BOARD
SET DELETED = 'Y'
WHERE POST_NO = ${postNo}
